import React, { useState } from 'react';
import axios from 'axios';

const PasswordResetRequestForm = () => {
    const [email, setEmail] = useState('');

    const handleChange = (e) => {
        setEmail(e.target.value);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://127.0.0.1:8000/users/api/PasswordResetRequest/', { email }, {
                headers: { 'Content-Type': 'application/json' }
            });
            alert(response.data.message);
        } catch (error) {
            console.error('Error requesting password reset:', error);
            alert('Error requesting password reset');
        }
    };

    return (
        <div className="container">
            <h2>Password Reset Request</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Email</label>
                    <input type="email" className="form-control" value={email} onChange={handleChange} placeholder="Email" required />
                </div>
                <button type="submit" className="btn btn-primary">Request Reset</button>
            </form>
        </div>
    );
};

export default PasswordResetRequestForm;
