import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const LoginForm = ({ setToken }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState(''); // حالة لرسالة الخطأ
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
  
    try {
      const response = await axios.post('http://127.0.0.1:8000/users/api/login/', {
        email,
        password,
      });
  
      // تعديل هنا: استخراج الرمز المميز مباشرةً من الاستجابة
      const { token } = response.data; 
  
      if (token) {
        localStorage.setItem('authToken', token);
        setToken(token);
        navigate('/');
      } else {
        setErrorMessage('No token received'); // يمكنك تخصيص هذه الرسالة
      }
    } catch (error) {
      // ... (باقي معالجة الأخطاء كما هي)
    }
  };

  return (
    <form onSubmit={handleLogin}>
      <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
      <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} required />
      {errorMessage && <div className="error-message">{errorMessage}</div>} {/* عرض رسالة الخطأ */}
      <button type="submit">Login</button>
    </form>
  );
};

export default LoginForm;
