import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import CreateUserForm from './components/CreateUserForm';
import LoginForm from './components/LoginForm';
import PasswordResetRequestForm from './components/PasswordResetRequestForm';
import SetNewPasswordForm from './components/SetNewPasswordForm';
import HomePage from './components/HomePage'; // الصفحة الرئيسية التي يتم توجيه المستخدم إليها بعد تسجيل الدخول

function App() {
    const [token, setToken] = useState(() => localStorage.getItem('authToken') || null);

    useEffect(() => {
        axios.defaults.headers.common['Authorization'] = token ? `Bearer ${token}` : '';
    }, [token]);

    return (
        <Router>
            <div className="App">
                <div className="container">
                    <h1 className="my-4">Gym Management System</h1>
                    <nav>
                        <ul className="nav">
                            <li className="nav-item">
                                <Navigate to="/login" element={<LoginForm setToken={setToken} />} />
                            </li>
                        </ul>
                    </nav>
                    <Routes>
                        <Route path="/login" element={<LoginForm setToken={setToken} />} />
                        <Route path="/" element={token ? <HomePage /> : <Navigate to="/login" />} />
                        <Route path="/create-user" element={token ? <CreateUserForm /> : <Navigate to="/login" />} />
                        <Route path="/password-reset-request" element={token ? <PasswordResetRequestForm /> : <Navigate to="/login" />} />
                        <Route path="/set-new-password" element={token ? <SetNewPasswordForm /> : <Navigate to="/login" />} />
                    </Routes>
                </div>
            </div>
        </Router>
    );
}

export default App;
