import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, Navigate } from 'react-router-dom';
import CreateUserForm from './components/CreateUserForm';
import LoginForm from './components/LoginForm';
import PasswordResetRequestForm from './components/PasswordResetRequestForm';
import SetNewPasswordForm from './components/SetNewPasswordForm';
import ProtectedRoute from './components/ProtectedRoute';

function App() {
    const [token, setToken] = useState(null);

    const isLoggedIn = token !== null;

    return (
        <Router>
            <div className="App">
                <div className="container">
                    <h1 className="my-4">Gym Management System</h1>
                    <nav>
                        <ul className="nav">
                            <li className="nav-item">
                                <Link className="nav-link" to="/login">Login</Link>
                            </li>
                            {/* Add more links if needed */}
                        </ul>
                    </nav>
                    <Routes>
                        <Route path="/login" element={<LoginForm setToken={setToken} />} />
                        <Route path="/create-user" element={<ProtectedRoute element={<CreateUserForm />} isLoggedIn={isLoggedIn} />} />
                        <Route path="/password-reset-request" element={<ProtectedRoute element={<PasswordResetRequestForm />} isLoggedIn={isLoggedIn} />} />
                        <Route path="/set-new-password" element={<ProtectedRoute element={<SetNewPasswordForm />} isLoggedIn={isLoggedIn} />} />
                        <Route path="/" element={isLoggedIn ? <HomePage /> : <Navigate to="/login" />} />
                    </Routes>
                </div>
            </div>
        </Router>
    );
}

const HomePage = () => <h2>Welcome to the Gym Management System</h2>;

export default App;
