import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const LoginForm = ({ setToken }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [responseData, setResponseData] = useState(null);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setResponseData(null);

    try {
      const response = await axios.post('http://127.0.0.1:8000/users/api/login/', {
        email,
        password,
      }, {
        headers: {
          'Content-Type': 'application/json' // تحديد نوع المحتوى كـ JSON
        }
      });

      const { token } = response.data;

      if (token) {
        localStorage.setItem('authToken', token);
        setToken(token);
        navigate('/');
      } else {
        setResponseData(response.data); 
      }
    } catch (error) {
      setResponseData(error.response?.data || { message: 'Login failed. Please try again later.' });
    }
  };

  return (
    <form onSubmit={handleLogin}>
      <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
      <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} required />

      {responseData && (
        <div>
          <p>Server Response:</p>
          <pre>{JSON.stringify(responseData, null, 2)}</pre>
        </div>
      )}

      <button type="submit">Login</button>
    </form>
  );
};

export default LoginForm;
